import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import ScrumDetails from '../ScrumDetails/ScrumDetails';
import { UserContext } from '../../context/UserContext';

const Dashboard = () => {
    const { user } = useContext(UserContext);
    const [scrumTeams, setScrumTeams] = useState([]);
    const [selectedScrum, setSelectedScrum] = useState(null);
    const [showForm, setShowForm] = useState(false);
    const [teamMembers, setTeamMembers] = useState([]);
    const [formData, setFormData] = useState({
        scrumName: '',
        taskTitle: '',
        taskDescription: '',
        taskStatus: 'To Do',
        assignedUser: '',
    });

    useEffect(() => {
        // Fetch scrum teams and users in parallel
        Promise.all([
            axios.get('http://localhost:4000/scrums'),
            axios.get('http://localhost:4000/users'),
        ])
            .then(([scrumResponse, userResponse]) => {
                setScrumTeams(scrumResponse.data);
                setTeamMembers(userResponse.data);
            })
            .catch((error) => console.error('Error loading data:', error));
    }, []);

    const fetchScrumDetails = (scrumId) => {
        axios
            .get(`http://localhost:4000/scrums/${scrumId}`)
            .then((response) => setSelectedScrum(response.data))
            .catch((error) => console.error('Error fetching scrum details:', error));
    };

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleFormSubmit = async (e) => {
        e.preventDefault();
        try {
            const scrumResponse = await axios.post('http://localhost:4000/scrums', {
                name: formData.scrumName,
            });

            await axios.post('http://localhost:4000/tasks', {
                title: formData.taskTitle,
                description: formData.taskDescription,
                status: formData.taskStatus,
                scrumId: scrumResponse.data.id,
                assignedTo: formData.assignedUser,
            });

            const updatedScrums = await axios.get('http://localhost:4000/scrums');
            setScrumTeams(updatedScrums.data);
            setShowForm(false);
            setFormData({ scrumName: '', taskTitle: '', taskDescription: '', taskStatus: 'To Do', assignedUser: '' });
        } catch (error) {
            console.error('Error creating scrum:', error);
        }
    };

    return (
        <div>
            <h2>Scrum Dashboard</h2>
            {user?.role === 'admin' && (
                <div>
                    <button onClick={() => setShowForm((prev) => !prev)}>
                        {showForm ? 'Cancel' : 'Add New Scrum'}
                    </button>
                    {showForm && (
                        <form onSubmit={handleFormSubmit}>
                            {Object.entries({
                                scrumName: 'Scrum Name',
                                taskTitle: 'Task Title',
                                taskDescription: 'Task Description',
                            }).map(([key, label]) => (
                                <div key={key}>
                                    <label>{label}:</label>
                                    <input
                                        type="text"
                                        name={key}
                                        value={formData[key]}
                                        onChange={handleChange}
                                        required
                                    />
                                </div>
                            ))}
                            <div>
                                <label>Status:</label>
                                <select name="taskStatus" value={formData.taskStatus} onChange={handleChange} required>
                                    <option value="To Do">To Do</option>
                                    <option value="In Progress">In Progress</option>
                                    <option value="Done">Done</option>
                                </select>
                            </div>
                            <div>
                                <label>Assign To:</label>
                                <select name="assignedUser" value={formData.assignedUser} onChange={handleChange} required>
                                    <option value="">Select a user</option>
                                    {teamMembers.map((member) => (
                                        <option key={member.id} value={member.id}>
                                            {member.name} ({member.email})
                                        </option>
                                    ))}
                                </select>
                            </div>
                            <button type="submit">Create Scrum</button>
                        </form>
                    )}
                </div>
            )}
            <ul>
                {scrumTeams.map((scrum) => (
                    <li key={scrum.id}>
                        {scrum.name}
                        <button onClick={() => fetchScrumDetails(scrum.id)}>View Details</button>
                    </li>
                ))}
            </ul>
            {selectedScrum && <ScrumDetails scrum={selectedScrum} />}
        </div>
    );
};

export default Dashboard;
