import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import ScrumDetails from '../Scrum Details/ScrumDetails';
import { UserContext } from '../../context/UserContext';

const Dashboard = () => {
    const [scrums, setScrums] = useState([]);
    const [selectedScrum, setSelectedScrum] = useState(null);
    const [showForm, setShowForm] = useState(false);
    const [users, setUsers] = useState([]);
    const [formData, setFormData] = useState({
        scrumName: '',
        taskTitle: '',
        taskDescription: '',
        taskStatus: 'To Do',
        taskAssignedTo: '',
    });

    const { user } = useContext(UserContext);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [scrumsResponse, usersResponse] = await Promise.all([
                    axios.get('http://localhost:4000/scrums'),
                    axios.get('http://localhost:4000/users'),
                ]);
                setScrums(scrumsResponse.data);
                setUsers(usersResponse.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }, []);

    const handleGetDetails = async (scrumId) => {
        try {
            const response = await axios.get(`http://localhost:4000/scrums/${scrumId}`);
            setSelectedScrum(response.data);
        } catch (error) {
            console.error('Error fetching scrum details:', error);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({ ...prevData, [name]: value }));
    };

    const handleAddScrum = async (event) => {
        event.preventDefault();

        try {
            const { scrumName, taskTitle, taskDescription, taskStatus, taskAssignedTo } = formData;

            // Add new Scrum
            const { data: newScrum } = await axios.post('http://localhost:4000/scrums', {
                name: scrumName,
            });

            // Add new Task
            await axios.post('http://localhost:4000/tasks', {
                title: taskTitle,
                description: taskDescription,
                status: taskStatus,
                scrumId: newScrum.id,
                assignedTo: taskAssignedTo,
                history: [{ status: taskStatus, date: new Date().toISOString().split('T')[0] }],
            });

            // Fetch updated scrum list
            const { data: updatedScrums } = await axios.get('http://localhost:4000/scrums');
            setScrums(updatedScrums);

            // Reset form
            setFormData({ scrumName: '', taskTitle: '', taskDescription: '', taskStatus: 'To Do', taskAssignedTo: '' });
            setShowForm(false);
        } catch (error) {
            console.error('Error adding scrum:', error);
        }
    };

    return (
        <div>
            <h2>Scrum Teams</h2>

            {user?.role === 'admin' && (
                <div>
                    <button onClick={() => setShowForm((prev) => !prev)}>
                        {showForm ? 'Cancel' : 'Add New Scrum'}
                    </button>

                    {showForm && (
                        <form onSubmit={handleAddScrum}>
                            <div>
                                <label>Scrum Name:</label>
                                <input
                                    type="text"
                                    name="scrumName"
                                    value={formData.scrumName}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div>
                                <label>Task Title:</label>
                                <input
                                    type="text"
                                    name="taskTitle"
                                    value={formData.taskTitle}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div>
                                <label>Task Description:</label>
                                <input
                                    type="text"
                                    name="taskDescription"
                                    value={formData.taskDescription}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div>
                                <label>Task Status:</label>
                                <select name="taskStatus" value={formData.taskStatus} onChange={handleInputChange}>
                                    <option value="To Do">To Do</option>
                                    <option value="In Progress">In Progress</option>
                                    <option value="Done">Done</option>
                                </select>
                            </div>
                            <div>
                                <label>Assign To:</label>
                                <select name="taskAssignedTo" value={formData.taskAssignedTo} onChange={handleInputChange} required>
                                    <option value="">Select a user</option>
                                    {users.map((user) => (
                                        <option key={user.id} value={user.id}>
                                            {user.name} ({user.email})
                                        </option>
                                    ))}
                                </select>
                            </div>
                            <button type="submit">Create Scrum</button>
                        </form>
                    )}
                </div>
            )}

            <ul>
                {scrums.map((scrum) => (
                    <li key={scrum.id}>
                        {scrum.name}
                        <button onClick={() => handleGetDetails(scrum.id)}>Get Details</button>
                    </li>
                ))}
            </ul>

            {selectedScrum && <ScrumDetails scrum={selectedScrum} />}
        </div>
    );
};

export default Dashboard;
