import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import ScrumDetails from '../ScrumDetails/ScrumDetails';
import { UserContext } from '../../context/UserContext';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

const Dashboard = () => {
    const { user } = useContext(UserContext);
    const [scrumTeams, setScrumTeams] = useState([]);
    const [selectedScrum, setSelectedScrum] = useState(null);
    const [showForm, setShowForm] = useState(false);
    const [teamMembers, setTeamMembers] = useState([]);

    useEffect(() => {
        Promise.all([
            axios.get('http://localhost:4000/scrums'),
            axios.get('http://localhost:4000/users'),
        ])
            .then(([scrumResponse, userResponse]) => {
                setScrumTeams(scrumResponse.data);
                setTeamMembers(userResponse.data);
            })
            .catch((error) => console.error('Error loading data:', error));
    }, []);

    const fetchScrumDetails = (scrumId) => {
        axios
            .get(`http://localhost:4000/scrums/${scrumId}`)
            .then((response) => setSelectedScrum(response.data))
            .catch((error) => console.error('Error fetching scrum details:', error));
    };

    const validationSchema = Yup.object({
        scrumName: Yup.string().required('Scrum Name is required'),
        taskTitle: Yup.string().required('Task Title is required'),
        taskDescription: Yup.string().required('Task Description is required'),
        taskStatus: Yup.string().required('Task Status is required'),
        assignedUser: Yup.string().required('Please select a user'),
    });

    const handleFormSubmit = async (values, { resetForm }) => {
        try {
            const scrumResponse = await axios.post('http://localhost:4000/scrums', {
                name: values.scrumName,
            });

            await axios.post('http://localhost:4000/tasks', {
                title: values.taskTitle,
                description: values.taskDescription,
                status: values.taskStatus,
                scrumId: scrumResponse.data.id,
                assignedTo: values.assignedUser,
            });

            const updatedScrums = await axios.get('http://localhost:4000/scrums');
            setScrumTeams(updatedScrums.data);
            setShowForm(false);
            resetForm();
        } catch (error) {
            console.error('Error creating scrum:', error);
        }
    };

    return (
        <div>
            <h2>Scrum Dashboard</h2>
            {user?.role === 'admin' && (
                <div>
                    <button onClick={() => setShowForm((prev) => !prev)}>
                        {showForm ? 'Cancel' : 'Add New Scrum'}
                    </button>
                    {showForm && (
                        <Formik
                            initialValues={{
                                scrumName: '',
                                taskTitle: '',
                                taskDescription: '',
                                taskStatus: 'To Do',
                                assignedUser: '',
                            }}
                            validationSchema={validationSchema}
                            onSubmit={handleFormSubmit}
                        >
                            <Form>
                                <div>
                                    <label>Scrum Name:</label>
                                    <Field type="text" name="scrumName" />
                                    <ErrorMessage name="scrumName" component="div" className="error" />
                                </div>
                                <div>
                                    <label>Task Title:</label>
                                    <Field type="text" name="taskTitle" />
                                    <ErrorMessage name="taskTitle" component="div" className="error" />
                                </div>
                                <div>
                                    <label>Task Description:</label>
                                    <Field type="text" name="taskDescription" />
                                    <ErrorMessage name="taskDescription" component="div" className="error" />
                                </div>
                                <div>
                                    <label>Status:</label>
                                    <Field as="select" name="taskStatus">
                                        <option value="To Do">To Do</option>
                                        <option value="In Progress">In Progress</option>
                                        <option value="Done">Done</option>
                                    </Field>
                                    <ErrorMessage name="taskStatus" component="div" className="error" />
                                </div>
                                <div>
                                    <label>Assign To:</label>
                                    <Field as="select" name="assignedUser">
                                        <option value="">Select a user</option>
                                        {teamMembers.map((member) => (
                                            <option key={member.id} value={member.id}>
                                                {member.name} ({member.email})
                                            </option>
                                        ))}
                                    </Field>
                                    <ErrorMessage name="assignedUser" component="div" className="error" />
                                </div>
                                <button type="submit">Create Scrum</button>
                            </Form>
                        </Formik>
                    )}
                </div>
            )}
            <ul>
                {scrumTeams.map((scrum) => (
                    <li key={scrum.id}>
                        {scrum.name}
                        <button onClick={() => fetchScrumDetails(scrum.id)}>View Details</button>
                    </li>
                ))}
            </ul>
            {selectedScrum && <ScrumDetails scrum={selectedScrum} />}
        </div>
    );
};

export default Dashboard;
